const body = $input.all()[0].json.body;

// Fallback for legacy calls
if (body.prompt && !body.student) {
  return { prompt: body.prompt };
}

const student = body.student || {};
const analysis = body.analysis || { trimestres: {} };
const config = body.config || {};
const raw_data = body.raw_data || {};

const trimestres = Object.keys(analysis.trimestres || {}).sort();
const dernierTrimestreKey = trimestres[trimestres.length - 1];
const dernierTrimestre = dernierTrimestreKey ? analysis.trimestres[dernierTrimestreKey] : {};
const numeroTrimestre = dernierTrimestreKey ? parseInt(dernierTrimestreKey.replace('trimestre', '')) : 3;

let prompt = `Tu es un professeur de ${config.matiere || 'matière'} en classe de ${config.niveau || 'niveau'}. Rédige une appréciation scolaire pour ${student.prenom || 'l\'élève'} ${student.nom || ''}.\n\n`;

prompt += `Données académiques:\n`;
trimestres.forEach(t => {
    const data = analysis.trimestres[t];
    const trimNumber = t.replace('trimestre', '');
    prompt += `- Trimestre ${trimNumber}: Moyenne devoirs ${data.moyenneDevoirs?.toFixed(1) || 'N/A'}/20`;
    if (data.moyenneInterrogations !== null && data.nbInterrogations > 0) {
        prompt += `, interrogations ${data.moyenneInterrogations?.toFixed(1)}/20`;
    }
    prompt += `\n`;
});

prompt += `\nDétail des notes (Moyenne, Coefficient, Type):\n`;
trimestres.forEach(t => {
    const notes = raw_data[t];
    if (notes && notes.length > 0) {
        prompt += `Trimestre ${t.replace('trimestre', '')}: `;
        notes.forEach(n => {
            prompt += `${n.note}/${n.coeff} (${n.evaluationType || '?'}) `;
        });
        prompt += `\n`;
    }
});

if (analysis.evolution) {
    prompt += `- Évolution: ${analysis.evolution}\n`;
}

if (student.investment && student.investment.length > 0) {
    prompt += `\nComportement et investissement:\n`;
    student.investment.forEach(criterion => {
        prompt += `- ${criterion}\n`;
    });
}

let conseilsSpecifiques = '';
if (numeroTrimestre === 3) {
    conseilsSpecifiques = `\n- C'est le trimestre 3 (fin d'année), ne demande PAS de progresser ou de continuer les efforts\n- Fais un bilan de l'année scolaire\n- Pour les bons résultats : félicite et encourage pour la suite`;
} else {
    conseilsSpecifiques = `\n- C'est le trimestre ${numeroTrimestre}, tu peux encourager la progression et donner des conseils pour la suite\n- Donne des conseils constructifs`;
}

const ecartDevoirs = Math.abs((dernierTrimestre.moyenneDevoirs || 0) - (dernierTrimestre.moyenneGenerale || 0));
const mentionnerNoteDevoirs = ecartDevoirs > 3;

prompt += `\nInstructions de rédaction :\n`;
prompt += `- Mentionne les résultats aux devoirs sur table en priorité\n`;
if (mentionnerNoteDevoirs) {
    prompt += `- INDIQUE la note chiffrée aux devoirs sur table car écart significatif avec la moyenne générale\n`;
} else {
    prompt += `- NE MENTIONNE PAS la note chiffrée aux devoirs, parle seulement de "bons/très bons/excellents résultats" ou "difficultés"\n`;
}
prompt += `- Intègre le comportement/investissement si pertinent${conseilsSpecifiques}\n`;
prompt += `- Reste très concise (STRICTEMENT moins de 200 caractères)\n`;
prompt += `- IMPÉRATIF : L'appréciation DOIT être complète et se terminer par un point\n`;
prompt += `- Ne mentionne jamais les TP\n`;
prompt += `- Utilise un ton professionnel et bienveillant, tu vouvoies l\'élève\n`;
prompt += `- Écris directement l\'appréciation complète sans introduction ni explication.\n`;

return { prompt: prompt };
