const body = $input.all()[0].json.body;
const subjects = body.subjects || [];
const generalAvg = body.generalAvg || {};
const trimester = body.trimester || '1';

let prompt = `Tu es le Professeur Principal d'un élève. Rédige une appréciation générale pour son bulletin du trimestre ${trimester}.\n\n`;

prompt += `Moyenne générale de l'élève : ${generalAvg.student || 'N/A'}.\n\n`;

prompt += "Voici le détail par matière :\n";

subjects.forEach(sub => {
    let subjectInfo = `- ${sub.name}`;
    if (sub.teacher && sub.teacher !== 'N/A') subjectInfo += ` (${sub.teacher})`;
    if (sub.hours) subjectInfo += ` [${sub.hours}]`;
    subjectInfo += `: Note ${sub.grades.student || 'N/A'}`;
    if (sub.grades.class) subjectInfo += ` (Moy. classe: ${sub.grades.class})`;
    subjectInfo += `.\n  Appréciation: "${sub.appreciation}"\n`;
    prompt += subjectInfo;
});

prompt += `\nConsignes :\n- Synthétise les points forts et les axes d'amélioration.\n- Adopte un ton professionnel, encourageant et constructif.\n- Ne répète pas le détail de chaque matière, mais dégage une tendance générale.\n- Fais ressortir l'attitude et l'investissement global.\n- Si c'est le 3ème trimestre, fais un bilan de l'année.\n- Longueur : environ 3-4 phrases.\n`;

return { prompt };
